import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [formData, setFormData] = useState({
    location: "",
    min_temp: "",
    max_temp: "",
    humidity: "",
    wind_speed: "",
    wind_direction: "",
    pressure: "",
    rain_today: "No",
  });

  const [prediction, setPrediction] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8000/predict", formData);
      setPrediction(response.data.rain_tomorrow);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Rain Prediction</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="location" placeholder="Location" onChange={handleChange} required />
        <input type="number" name="min_temp" placeholder="Min Temp (°C)" onChange={handleChange} required />
        <input type="number" name="max_temp" placeholder="Max Temp (°C)" onChange={handleChange} required />
        <input type="number" name="humidity" placeholder="Humidity (%)" onChange={handleChange} required />
        <input type="number" name="wind_speed" placeholder="Wind Speed (km/h)" onChange={handleChange} required />
        <input type="text" name="wind_direction" placeholder="Wind Direction (N/S/E/W)" onChange={handleChange} required />
        <input type="number" name="pressure" placeholder="Pressure (hPa)" onChange={handleChange} required />
        <select name="rain_today" onChange={handleChange}>
          <option value="No">No</option>
          <option value="Yes">Yes</option>
        </select>
        <button type="submit">Predict</button>
      </form>

      {prediction && <h3>Will it rain tomorrow? {prediction}</h3>}
    </div>
  );
};

export default App;
